#!/bin/bash
# Mock 2 Question 6

k=kubectl

cat << EOF | kubectl apply -f -
apiVersion: certificates.k8s.io/v1
kind: CertificateSigningRequest
metadata:
  name: john-user
spec:
  request: 
  signerName: kubernetes.io/kube-apiserver-client  
  usages:
  - digital signature
  - key encipherment
  - server auth
EOF

$k certificate approve john-user

$k create role developer --resource=pods --verb=create,list,get,update,delete --namespace=development

$k create rolebinding developer-role-binding --role=developer --user=john-user --namespace=development
